//test_dnasearch.c

//Contains main function that tests each function in dnasearch.c. USE ASSERT
#include "dnasearch.h"
#include "dnasearch.h"
#include <string.h>
#include <ctype.h>
#include <stdlib.h>
#include <stdio.h>
#include <assert.h>

int main(void) {
  int pmatchres;
  int* totaloffsetarr;
  int makearrres;
  int outputres;
  int pcheckres;
  int plen;
  int tlen;
  char seqarray[15000];
  
  FILE* fptr;
  fptr = fopen("test.txt","w");

  if (fptr == NULL) {
    printf("file couldn't be opened");
    return -1; //indicate error
  }

  //invalid edge case 15001 characters
  char ecase[] = "";
  int writeres = fputs(ecase, fptr);
  if (writeres == EOF) {
    printf("failed to write to file");
    return -1;
  }
  fclose(fptr);

  FILE* filep;
  filep = fopen("test.txt", "r");

  if(filep == NULL) {
    printf("Error!");
    return -1; //indicate error
  }
  
  char pattern[] = "   ag gt gT ACC";
  plen = strlen(pattern);
  
  //checks file input and other functions
  assert((makearrres = makearr(seqarray, filep)) == -1);
  assert((pcheckres = pcheck(pattern, plen, makearrres)) == -1);
  assert((pmatchres = pattern_match(seqarray, makearrres, pattern, plen, 0)) == -1);

  //invalid pattern
  char pattern2[] = " a % G";
  char sequence2[] = "aaaagggggGgG";
  plen = strlen(pattern2);
  tlen = strlen(sequence2);
  
  assert((pcheckres = pcheck(pattern2, plen, tlen)) == -1);
  assert((pmatchres = pattern_match(sequence2, tlen, pattern2, plen, 0)) == -1);
  totaloffsetarr = total_offset(sequence2, pattern2);
  assert((outputres = totalout(totaloffsetarr)) == 1);

  //invalid pattern
  char pattern3[] = "aaaaaaaaaaaaaaaa";
  char sequence3[] = "aaaaaaaaaa";
  plen = strlen(pattern3);
  tlen = strlen(sequence3);
  
  assert((pcheckres = pcheck(pattern3, plen, tlen)) == -1);
  assert((pmatchres = pattern_match(sequence3, tlen, pattern3, plen, 0)) == -1);

  char pattern4[] = "A";
  char sequence4[] = "AAGGCCAAGGCCTT";
  plen = strlen(pattern4);
  tlen = strlen(sequence4);
    
  assert((pcheckres = pcheck(pattern4, plen, tlen)) != -1);
  assert((pmatchres = pattern_match(sequence4, tlen, pattern4, plen, 0)) != -1);
  totaloffsetarr = total_offset(sequence4, pattern4);
  assert((outputres = totalout(totaloffsetarr)) == 1);
}
